body.script:
$('.smooth').click(function() {
	var sectionTo = $(this).attr('href');
	$('html, body').animate({
	scrollTop: $(sectionTo).offset().top
	}, 1500);
});



$( document ).ready(function() {

	$.getJSON('https://ipinfo.io/', function(data){

			console.log(data)
			if (data.country=="AR")
			{
				$('#div_precio_ar').show();
				$('#pais').val('ARGENTINA');
			}else{
				$('#div_precio_resto').show();
			}

		});
});
